mapper.select:
SELECT ID, CAT, MAP, DOMAIN, CREATED
FROM map
ORDER BY #{orderBy}
LIMIT #{start}, #{end}

SELECT ID, CAT, MAP, DOMAIN, CREATED
FROM map
WHERE  
DOMAIN = #{domain}
AND CAT = #{cat}

SELECT ID, CAT, MAP, DOMAIN, CREATED
FROM map
WHERE DOMAIN = #{domain}
ORDER BY #{orderBy}
LIMIT #{start}, #{end}

SELECT ID, CAT, MAP, DOMAIN, CREATED
FROM map
WHERE ID = #{id}

SELECT count(*) 
FROM map
WHERE  1 = 1

    AND DOMAIN = #{domain}


    AND CAT = #{cat}


    AND MAP = #{map}

mapper.update:
UPDATE map
SET 
DOMAIN = #{domain},
CAT = #{cat},
MAP = #{map}
WHERE ID = #{id}
