mapper.select:
SELECT ID, TITLE, DOMAIN, COMPANY_NAME as companyName, COMPANY_OVERVIEW as companyOverview, COMPANY_ADDRESS as companyAddress, COMPANY_RANGE as companyRange, JOB_CATEGORY as jobCategory, JOB_LOCATION as jobLocation, JOB_TIME_WORK as jobTimeWork, JOB_MEMBER_LEVEL as jobMemberLevel, JOB_SALARY as jobSalary, JOB_AGE as jobAge, JOB_SEX as jobSex, JOB_OVERVIEW as jobOverview, JOB_EDUCATION_LEVEL as jobEducationLevel, JOB_EXPERIENCE_LEVEL as jobExperienceLevel, JOB_REQUIREMENT as jobRequirement, JOB_LANGUAGE as jobLanguage, JOB_CONTACT_DETAIL as jobContactDetail, JOB_CONTACT_NAME as jobContactName, JOB_CONTACT_ADDRESS as jobContactAddress, JOB_CONTACT_PERSON as jobContactPerson, JOB_CONTACT_PHONE as jobContactPhone, JOB_CONTACT_EMAIL as jobContactEmail, STATUS as status, JOB_CREATED as created, JOB_CREATER as creater, JOB_UPDATED as updated, JOB_UPDATER as updater, JOB_PUBLISHED as published, JOB_EXPIRED as expired FROM job_crawl
WHERE DOMAIN = #{domain}
ORDER BY JOB_CREATED DESC LIMIT #{start},#{end}

SELECT ID, TITLE, DOMAIN, COMPANY_NAME as companyName, COMPANY_OVERVIEW as companyOverview, COMPANY_ADDRESS as companyAddress, COMPANY_RANGE as companyRange, JOB_CATEGORY as jobCategory, JOB_LOCATION as jobLocation, JOB_TIME_WORK as jobTimeWork, JOB_MEMBER_LEVEL as jobMemberLevel, JOB_SALARY as jobSalary, JOB_AGE as jobAge, JOB_SEX as jobSex, JOB_OVERVIEW as jobOverview, JOB_EDUCATION_LEVEL as jobEducationLevel, JOB_EXPERIENCE_LEVEL as jobExperienceLevel, JOB_REQUIREMENT as jobRequirement, JOB_LANGUAGE as jobLanguage, JOB_CONTACT_DETAIL as jobContactDetail, JOB_CONTACT_NAME as jobContactName, JOB_CONTACT_ADDRESS as jobContactAddress, JOB_CONTACT_PERSON as jobContactPerson, JOB_CONTACT_PHONE as jobContactPhone, JOB_CONTACT_EMAIL as jobContactEmail, STATUS as status, JOB_CREATED as created, JOB_CREATER as creater, JOB_UPDATED as updated, JOB_UPDATER as updater, JOB_PUBLISHED as published, JOB_EXPIRED as expired FROM job_crawl WHERE 1 = 1
AND DOMAIN = #{domain}
AND JOB_CREATER = #{username} ORDER BY JOB_CREATED DESC LIMIT #{start},#{end}

SELECT count(*) 
FROM job_crawl

    WHERE DOMAIN = #{domain}

SELECT count(*) 
FROM job_crawl
WHERE 1 = 1

    AND DOMAIN = #{domain}

AND JOB_CREATER = #{username}

mapper.update:
UPDATE job_crawl
SET
JOB_CATEGORY = #{jobCategory},
JOB_UPDATED = #{updated},
JOB_UPDATER = #{updater}
WHERE ID = #{id}

UPDATE job_crawl
SET
JOB_LOCATION = #{jobLocation},
JOB_UPDATED = #{updated},
JOB_UPDATER = #{updater}
WHERE ID = #{id}
