mapper.update:
UPDATE job_education
SET
SCHOOL_NAME = #{schoolName},
SCHOOL_FIELD_OF_STUDY = #{schoolFieldOfStudy},
START_YEAR = #{startYear},
END_YEAR = #{endYear},
DESCRIPTION = #{description},
UPDATED = #{updated}
WHERE ID = #{id}
AND PROFILE_ID = #{profileId}

mapper.delete:
DELETE FROM job_education 
WHERE ID = #{id}
AND PROFILE_ID = #{profileId}
